import React from 'react'
import withStyles from '@material-ui/core/styles/withStyles'
import Card from '@material-ui/core/Card'
import dayjs from 'dayjs'
import relativeTime from 'dayjs/plugin/relativeTime'
import CardContent from '@material-ui/core/CardContent'
import CardMedia from '@material-ui/core/CardMedia'
import { ThumbUp, ThumbDown } from '@material-ui/icons'

import Typography  from '@material-ui/core/Typography';

const styles = {
    card: {
        display: 'flex',
        marginBottom: 20,
        marginRight: 20
    },
    image: {
        minWidth: 200,
        width: 400
    },
    content: {
        padding: 25,
        objectFit: 'cover'
    }
}

class Shout extends React.Component{


    likeShout = (e) => {
        console.log( "Like Shout", e)
    }

    dislikeShout = (e) => {
        console.log("disLike Shout", e.target.name)
    }

    render(){
        dayjs.extend(relativeTime)
        // console.log(this.props)
        const { classes, shout } = this.props
        return(
            <div>
                <Card className={classes.card}>
                    <CardMedia className = {classes.image}
                    image={shout.user.imgUrl} title='Profile Image'/>

                    <CardContent className={classes.content}>
                        <Typography variant="h5">
                            {shout.body}
                        </Typography>

                        <Typography variant="body2" color="textSecondary">
                             {dayjs(shout.created_at).fromNow()}
                        </Typography>

                        <Typography variant="body1" color="textSecondary">
                            @{shout.user.username}
                        </Typography>

                        <ThumbUp label={shout.id} onClick={(event) => this.likeShout(event)} />
                        <ThumbDown name={shout.id} onClick={this.dislikeShout} />
                    </CardContent>
                </Card>  
            </div>
        )
    }
}

export default withStyles(styles)(Shout);
