import React from 'react'
import withStyles from '@material-ui/core/styles/withStyles'
import Card from '@material-ui/core/Card'
import dayjs from 'dayjs'
import relativeTime from 'dayjs/plugin/relativeTime'
import CardContent from '@material-ui/core/CardContent'
import CardMedia from '@material-ui/core/CardMedia'
import IconButton from '@material-ui/core/IconButton'
import { ThumbUp, ThumbDown } from '@material-ui/icons'

import Typography  from '@material-ui/core/Typography';

const styles = {
    card: {
        display: 'flex',
        marginBottom: 20,
        marginRight: 20
    },
    image: {
        minWidth: 200,
        width: 400
    },
    content: {
        padding: 25,
        objectFit: 'cover'
    }
}

class Shout extends React.Component{

    constructor(){
        super()
        this.state = {
            shout: {}
        }
    }

    componentDidMount(){

        this.setState({
            shout: this.props.shout
        })
    }

    likeShout = (shoutId) => {
        const token = localStorage.getItem('token')
        const postObj = {
            method: 'POST',
            headers: {'Content-Type': 'application/json', 'Authorization': `Bearer ${token}`},
            body: JSON.stringify({shoutId: shoutId})
        }
        fetch(`http://localhost:3000/likes`, postObj)
        .then(resp => resp.json())
        .then(shout => console.log(shout))
    }

    dislikeShout = (shoutId) => {

        const token = localStorage.getItem('token')
        const deleteObj = {
            method: 'DELETE',
            headers: {'Content-Type': 'application/json', 'Authorization': `Bearer ${token}`},
            body: JSON.stringify({shoutId: shoutId})
        }
        fetch(`http://localhost:3000/unlike`, deleteObj)
        .then(resp => resp.json())
        .then(data => console.log(data))

    }

    render(){
        dayjs.extend(relativeTime)
        console.log("Shout state", this.state)
        const { classes, shout } = this.props
        const { id } = this.props.shout 
        return(
            <div>
                <Card className={classes.card}>
                    <CardMedia className = {classes.image}
                    image={shout.user.imgUrl} title='Profile Image'/>

                    <CardContent className={classes.content}>
                        <Typography variant="h5">
                            {shout.body}
                        </Typography>

                        <Typography variant="body2" color="textSecondary">
                             {dayjs(shout.created_at).fromNow()}
                        </Typography>

                        <Typography variant="body1" color="textSecondary">
                            @{shout.user.username}
                        </Typography>

                         <Typography variant="body2" color="textSecondary">
                            {shout.likeCount} likes
                        </Typography>

                         <Typography variant="body2" color="textSecondary">
                            {shout.commentCount} comments
                        </Typography>



                        <IconButton aria-label="like">
                            <ThumbUp onClick={() => this.likeShout(id)} />
                        </IconButton>

                        <IconButton aria-label="dislike">
                            <ThumbDown  onClick={() => this.dislikeShout(id)} />
                        </IconButton>

                    </CardContent>
                </Card>  
            </div>
        )
    }
}

export default withStyles(styles)(Shout);
