import React from 'react'
import editDetails from './editDetails'

import PropTypes from 'prop-types';
import withStyles from '@material-ui/core/styles/withStyles';

// MUI
import Button from '@material-ui/core/Button'
import Paper from '@material-ui/core/Paper'
//Icons




class Profile extends React.Component{
    constructor(){
        super()
        this.state = {
           renderState:false,
           userInfo: {

           }
        }
    }

    componentDidMount(){
        //    
        if(localStorage.getItem !== null){
            const token = localStorage.getItem('token')
            const reqObj = {
                method: 'GET',
                headers: {'Content-Type': 'application/json', 'Authorization': `Bearer ${token}`}
            }
            fetch('http://localhost:3000/current_user', reqObj)
            .then(resp => resp.json())
            .then(data => 

                   { this.setState({
                       userInfo: data, renderState: 'done'
                         })
                    }
                )

              }
    }

    fetchUserInfo = () => {


    }


    renderUserInfo = () => {
        // console.log(this.state)
        const styles = theme => ({
            paper: {padding: 20},
            profile: {
                '& .image-wrapper': {
                    textAlign: 'center', 
                    position: 'relative',
                    '& button': {
                        position: 'absolute',
                        top: '80%',
                        left: '70%'
                    }
                },
                '& .profile-image':{
                    width: 200,
                    height: 200,
                    objectFit: 'cover',
                    maxWidth: '100%',
                    borderRadius: '50%'
                },
                '& .profile-details': {
                    textAlign: 'center',
                    '& span, svg': {
                        versionAlign: 'middle'
                    },
                    '& a':{
                        color: theme.palette.primary.main
                    }
                }, 
                '& hr': {
                    border: 'none',
                    margin: '0 0 10px 0'
                },
                '& svg.button': {
                    '&:hover':{
                        cursor: 'pointer'
                    }
                }
            },
            buttons: {
                textAlign: 'center',
                '& a': {
                    margin: '20px, 10px'
                }
            }
        })

        const { classes } = this.props

        return <Paper className={classes.paper}>
            <div className= {classes.profile}>
                <div className='profile-image'>
                    <img src={this.state.userInfo.imgUrl} alt=""/>
                </div>
                <h3>{this.state.userInfo.bio}</h3>
                <h3>{this.state.userInfo.username}</h3>
            </div>
        </Paper>
    }

    renderEditForm = () => {

    }

    loadingMessage = () => {
        return <h3>...Loading</h3>
    }

    render(){
        console.log(this.state)


        return(
            <div>
                <h2>Profile</h2>
                {/* {this.state.renderState !== false ? this.fetchUserInfo() : this.loadingMessage()} */}
                {this.state.renderState === 'done' ? this.renderUserInfo() : this.loadingMessage()}
            </div>
        )
    }
}

export default (withStyles(styles)(Profile))
