import React from 'react'
import NavBar from './NavBar'
import Findfollow from './Findfollow'
import Shout from './Shout'
import Grid  from '@material-ui/core/Grid';


class MyShouts extends React.Component{

    constructor(){
        super()
        this.state = {
            shouts: [],
            comments: [],
            rendered: false
        }
    }

    componentDidMount(){
        const token = localStorage.getItem('token')
            const reqObj = {
                method: 'GET',
                headers: {'Content-Type': 'application/json', 'Authorization': `Bearer ${token}`}
            }

            fetch('http://localhost:3000/myuser', reqObj)
            .then(resp => resp.json())
            .then(data => {
                // console.log(data)
                this.setState({
                    shouts: data.shouts, comments: data.comments, rendered: true
                    })
                }    
            )
    }



    render(){
        console.log("MyShouts state", this.state)
        return(
            <div>

               <div>
                   My Shouts
               </div>

               <div>
                   My Comments
               </div>
            </div>
        )
    }
}

export default MyShouts
