import React from 'react'
import NavBar from './NavBar'
import Findfollow from './Findfollow'
import Shout from './Shout'
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent'
import Typography  from '@material-ui/core/Typography';
import Grid  from '@material-ui/core/Grid';
import dayjs from 'dayjs'
import relativeTime from 'dayjs/plugin/relativeTime'
import IconButton from '@material-ui/core/IconButton'
import { Delete } from '@material-ui/icons'


class MyShouts extends React.Component{

    constructor(){
        super()
        this.state = {
            shouts: [],
            comments: [],
            rendered: false
        }
    }

    // const token = localStorage.getItem('token')
    //     const reqObj = {
    //         method: 'DELETE',
    //         headers: {'Content-Type': 'application/json', 'Authorization': `Bearer ${token}`},
    //     }

    //     fetch(`http://localhost:3000/posts/${event.target.name}`, reqObj)
    //     .then(resp => resp.json())
    //     .then( data => {
    //         if(data.error){
    //         alert(data.error)}
    //         else{
    //           this.setState({
    //             ...this.state, posts: data.posts, rendered: true
    //           })
    deleteShout = (id) => {
        const token = localStorage.getItem('token')
        const reqObj = {
            method: 'DELETE',
            headers: {'Content-Type': 'application/json', 'Authorization': `Bearer ${token}`}

        }

        fetch(`http://localhost:3000/shouts/${id}`, reqObj)
        .then(resp => resp.json())
        .then(data => console.log(data))
    }

    componentDidMount(){
        const token = localStorage.getItem('token')
            const reqObj = {
                method: 'GET',
                headers: {'Content-Type': 'application/json', 'Authorization': `Bearer ${token}`}
            }

            fetch('http://localhost:3000/myuser', reqObj)
            .then(resp => resp.json())
            .then(data => {
                // console.log(data)
                this.setState({
                    shouts: data.shouts, comments: data.comments, rendered: true
                    })
                }    
            )
    }

    renderShouts = () => {
        const { shouts } = this.state           
        dayjs.extend(relativeTime)



        return shouts.map( shout => 
        <Card> 
            <CardContent> 
                        <IconButton aria-label="like">
                            <Delete onClick={() => this.deleteShout(shout.id)} />
                        </IconButton>

                     <Typography variant="h5">
                            {shout.body}
                    </Typography> 

                     <Typography variant="body2" color="textSecondary">
                            {shout.likeCount} likes
                        </Typography>

                        <Typography variant="body2" color="textSecondary">
                            {shout.commentCount} comments
                        </Typography>

                        <Typography variant="body2" color="textSecondary">
                             Created {dayjs(shout.created_at).fromNow()}
                        </Typography>

                        <Typography variant="body2" color="textSecondary">
                             Last Updated {dayjs(shout.updated_at).fromNow()}
                        </Typography>
                </CardContent>
        </Card>
        )
    }

    renderComments = () => {
        return <h1>Hello Comments</h1>
    }


    render(){
        console.log("MyShouts state", this.state)
        return(
            <div>

               <div>
                   {this.state.rendered === true ? this.renderShouts() : <p>Loading...</p>}
               </div>

               <div>
               {this.state.rendered === true ? this.renderComments() : <p>Loading...</p>}
               </div>
            </div>
        )
    }
}

export default MyShouts
