import React, {Component, Fragment} from 'react'
import ShoutButton from './ShoutButton'
import withStyles from '@material-ui/core/styles/withStyles'
import Button from '@material-ui/core/Button'
import TextField from '@material-ui/core/TextField'
import Dialog from '@material-ui/core/Dialog'
import DialogContent from '@material-ui/core/DialogContent'
import DialogTitle from '@material-ui/core/DialogTitle'
import { AddCircle, Close } from '@material-ui/icons';

const styles = theme => ({
    submitButton: {
        position: 'relative'
    }, 
    closeButton: {
        position: 'absolute',
        left: '90%',
        top: '10%'
    }
})

class PostShout extends Component{
    constructor(){
        super()
        this.state = {
            open: false,
            body: '',
             errors: {}
        }
    }

    handleOpen = () => {
        this.setState({open: true})
    }

    handleClose= () => {
        this.setState({open: false})
    }

    handleChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    handleSubmit = (event) => {
        event.preventDefault()
        this.props.PostShout({body: this.state.body})
    }

    render(){
        const { errors } = this.state
        const { classes } = this.props
        return(
            <Fragment>
                <ShoutButton onClick={this.handleOpen} tip="Post a Shout!">

                    <AddCircle />
                </ShoutButton>
                <Dialog open={this.state.open} onClose={this.handleClose} fullWidth maxWidth="sm">
                    <Button className= {classes.closeButton} >
                    <Close />
                    </Button>
                    <DialogTitle> 
                        Post a new Shout
                    </DialogTitle>

                    <DialogContent>
                        <form onSubmit={this.handleSubmit}>
                            <TextField name="body"
                            type="text"
                            label="Shout!"
                            multiline
                            rows="3"
                            placeholder="Shout!"
                            error = {errors.body ? true : false} 
                            helperText={errors.body}

                            onChange= {this.handleChange}
                            fullWidth
                            />
                            <Button type='submit' variant="contained"
                            >
                                Submit
                            </Button>
                        </form>
                    </DialogContent>
                </Dialog>
            </Fragment>
        )
    }
}


export default (withStyles(styles)(PostShout))
